import React, {useState} from "react"

const customers = [
    {
      id: 1,
      fullName: 'NVA',
      email: 'nva@co.cc',
      phone: '2345',
      gender:'male',
      city: 'Hue',
      address: '28 nguyễn Tri Phương',
    },
    {
      id: 2,
      fullName: 'NVB',
      email: 'nvb@co.cc',
      phone: '3456',
      gender:'female',
      city: 'Sai Gon',
      address: '100 nguyễn Tri Phương',
    },
  ];

const Student = () => {
    const [modalIsOpen, setIsOpen] = useState();
    const [customer, setCustomer] = useState({});

    return (
        <>
          <div className="container">
            <table
              className="table table-hover"
              style={{
                width: '100%',
              }}
            >
              <thead>
                <tr>
                  <th>ID</th>
                  <th>FullName</th>
                  <th>Email</th>
                  <th>Phone</th>
                  <th>Gender</th>
                  <th>City</th>
                  <th>Address</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {customers.map((item) => {
                  return (
                    <tr key={item.id}>
                      <td>{item.id}</td>
                      <td>{item.fullName}</td>
                      <td>{item.email}</td>
                      <td>{item.phone}</td>
                      <td>{item.gender}</td>
                      <td>{item.city}</td>
                      <td>{item.address}</td>
                      <td>
                        <button
                          className="btn btn-outline-secondary"
                          onClick={() => handleClickEditButton(item.id)}
                        >
                          Edit
                        </button>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
          <ModalUpdate
            modalIsOpen={modalIsOpen}
            closeModal={closeModal}
            data={customer}
            changeData={changeData}
          />
        </>
      );
    };

export default Student;
