import { useState } from "react"
import AddCustomer from "./AddCustomer";
import { Link } from "react-router-dom";
const customers = [
    {
        id: 1,
        fullName: "NVA",
        email: "nva@gmail.com",
        phone: "12345",
        gender: "male",
        city: "Huế",
        address: "28 NTP"
    },
    {
        id: 2,
        fullName: "NVB",
        email: "nvb@gmail.com",
        phone: "12345",
        gender: "male",
        city: "Huế",
        address: "28 NTP"
    },
]
// const handleAddCustomer(){
//     const newCustomers = [...customers];
// }
const Customer = () => {
    const [customerList, setCustomerList] = useState(customers);
    return (
        <div>
            <table
                className="table table-hover"
                style={{
                    width: '100%',
                }}
            >
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>FullName</th>
                        <th>Email</th>
                        <th>Phone</th>
                        <th>City</th>
                        <th>Address</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {customerList.map((item) => {
                        return (
                            <tr key={item.id}>
                                <td>{item.id}</td>
                                <td>{item.fullName}</td>
                                <td>{item.email}</td>
                                <td>{item.phone}</td>
                                <td>{item.city}</td>
                                <td>{item.address}</td>
                                <td>
                                    <Link to={"/add"}>
                                        <button className="btn btn-outline-primary me-2">
                                            Add
                                        </button>
                                    </Link>
                                    <button
                                        className="btn btn-outline-secondary"
                                    >
                                        Edit
                                    </button>
                                </td>
                            </tr>
                        );
                    })}
                </tbody>
            </table>
            <AddCustomer />
        </div>
    )
}
export default Customer
